mapper.update:
update qna set
qa_refStep=qa_refStep+1 where qa_ref=#{qa_ref} and
qa_refStep>#{qa_refStep}

mapper.select:
		SELECT * FROM (
    SELECT b.*, @rownum:=@rownum+1 AS rn
    FROM qna b, (SELECT @rownum:=0) AS tmp
    WHERE qa_ref IN (
        SELECT qa_ref FROM qna WHERE M_email = #{m_email}
    )
    ORDER BY qa_ref DESC, qa_refStep
	) AS boardlist
	WHERE rn BETWEEN #{startRow} and #{endRow}

mapper.update:
update qna set
qa_condition='답변완료'
where qa_no=#{qa_no}
