mapper.select:
select * from (select a.*, rowNum rn from (select * from QnA where qa_ref in 
	(select qa_ref from QnA where m_email=#{m_email}) order by qa_ref desc, qa_refStep) a)
		where rn between #{startRow} and #{endRow}

mapper.update:
update qna set
qa_condition='답변완료'
where qa_no=#{qa_no}
